using opensap.PurchaseOrder.Headers as Headers from '../db/data-model';
using opensap.PurchaseOrder.Items as Items from '../db/data-model';
using PURCHASE_ORDER_ITEM_VIEW as ItemsView from '../db/data-model';
using BUYER as BuyerView from '../db/data-model';
using USER_DETAILS as UserDetails from '../db/data-model';
using CURRENCY as Curr from '../db/data-model';

service CatalogService {

entity POs @(
		title: '{i18n>poService}',
		Capabilities: {
			InsertRestrictions: {Insertable: true},
			UpdateRestrictions: {Updatable: true},
			DeleteRestrictions: {Deletable: true}
		}
	) as projection on Headers;

entity POItems @(
		title: '{i18n>poService}',
		Capabilities: {
			InsertRestrictions: {Insertable: true},
			UpdateRestrictions: {Updatable: true},
			DeleteRestrictions: {Deletable: true}
		}
	) as projection on Items;

extend ItemsView with {
	PARTNERS: association to BuyerView on PARTNERS.PARTNERID = PARTNER_ID;
}
entity POItemsView @(
		title: '{i18n>poService}',
		Capabilities: {
			InsertRestrictions: {Insertable: false},
			UpdateRestrictions: {Updatable: false},
			DeleteRestrictions: {Deletable: false}
		}
	) as projection on ItemsView;

entity Buyer @(
		title: '{i18n>buyerService}',
		Capabilities: {
			InsertRestrictions: {Insertable: false},
			UpdateRestrictions: {Updatable: false},
			DeleteRestrictions: {Deletable: false}
		}
	) as projection on BuyerView;

entity User @(
		title: '{i18n>userService}',
		Capabilities: {
			InsertRestrictions: {Insertable: true},
			UpdateRestrictions: {Updatable: true},
			DeleteRestrictions: {Deletable: true}
		}
	) as projection on UserDetails;

entity CURRENCY @(
		title: '{i18n>currencySrvice}',
		Capabilities: {
			InsertRestrictions: {Insertable: false},
			UpdateRestrictions: {Updatable: false},
			DeleteRestrictions: {Deletable: false}
		}
	) as projection on Curr;
}

using from '../db/PurchaseOrder';
using from '../db/import';

entity Base {
  key ID : Integer;
}

namespace opensap.PurchaseOrder;

    type BusinessKey : String(10);
    type SDate : DateTime;
    type CurrencyT : String(5);
    type AmountT : Decimal(15, 2);
    type QuantityT : Decimal(13, 3);
    type UnitT : String(3);
    type StatusT : String(1);

	type HistoryT {
        CREATEDBY : BusinessKey;
        CREATEDAT : SDate;
        CHANGEDBY : BusinessKey;
        CHANGEDAT : SDate;
    };

    entity Headers {
        key PURCHASEORDERID : Integer;
            ITEMS           : association to many Items on ITEMS.POHeader = $self;
            HISTORY         : HistoryT;
            NOTEID          : BusinessKey null;
            PARTNER         : BusinessKey;
            CURRENCY        : CurrencyT;
            GROSSAMOUNT     : AmountT;
            NETAMOUNT       : AmountT;
            TAXAMOUNT       : AmountT;
            LIFECYCLESTATUS : StatusT;
            APPROVALSTATUS  : StatusT;
            CONFIRMSTATUS   : StatusT;
            ORDERINGSTATUS  : StatusT;
            INVOICINGSTATUS : StatusT;
    }

    entity Items {
        key POHeader     : association to Headers;
        key PRODUCT      : BusinessKey;
            NOTEID       : BusinessKey null;
            CURRENCY     : CurrencyT;
            GROSSAMOUNT  : AmountT;
            NETAMOUNT    : AmountT;
            TAXAMOUNT    : AmountT;
            QUANTITY     : QuantityT;
            QUANTITYUNIT : UnitT;
            DELIVERYDATE : SDate;
    }

	define view ItemView as
        select from Items
        {
            POHeader.PURCHASEORDERID,
            POHeader.PARTNER,
            PRODUCT,
            CURRENCY,
            GROSSAMOUNT,
            NETAMOUNT,
            TAXAMOUNT,
            QUANTITY,
            QUANTITYUNIT,
            DELIVERYDATE
        };

    type BusinessKey : String(10);
type SDate : DateTime;
type CurrencyT : String(5);
type AmountT : Decimal(15, 2);
type QuantityT : Decimal(13, 3);
type UnitT : String(3);
type StatusT : String(1);
    

@cds.persistence.exists
entity PURCHASE_ORDER_ITEM_VIEW {
  key PO_ITEM_ID: Integer;
  PARTNER_ID: BusinessKey;
  key PRODUCT_ID: BusinessKey;
  CURRENCY_CODE: CurrencyT;
  AMOUNT: AmountT;
  NET_AMOUNT: AmountT;
  TAX_AMOUNT: AmountT;
  QUANTITY: QuantityT;
  QUANTITY_UNIT: UnitT;
  DELIVERY_DATE: SDate;
}

@cds.persistence.exists 
Entity BUYER {
 	BUILDING: String(10); 
	CITY: String(40); 
	COMPANYNAME: String(80); 
	COUNTRY: String(3); 
	EMAILADDRESS: String(255); 
	LEGALFORM: String(10);  
	key PARTNERID: Integer; 
	PARTNERROLE: String(3);  
	POSTALCODE: String(10);  
	REGION: String(4);  
	STREET: String(60);  
}

@cds.persistence.exists 
Entity USER_DETAILS {
	 EMAIL: String(255);  
	 FIRSTNAME: String(40);
	 LASTNAME: String(40);
     key USERID: Integer;	 
}

@cds.persistence.exists 
Entity CURRENCY {
	 key CODE: String(3);
	 CURRENCY: String(80);
}
